import { createSlice } from "@reduxjs/toolkit";
let isLoggedIn = false;

const Auth = createSlice({
  name: "auth",
  initialState: isLoggedIn,
  reducers: {
    login(state, action) {
      state.isLoggedIn = true;
    },
  },
});
export default Auth.reducer;
