import { Col, Container, Row } from "react-bootstrap";
import { useState } from "react";

function Home() {
  const [file, setFile] = useState(null);
  const accepted = ["image/png", "image/jpeg", "image/jpg"];

  async function handleUpload(e) {
    const formData = new FormData();
    formData.append("file", file);
    e.preventDefault();
    let selected = e.target.files[0];
    //const token = localStorage.getItem("token");
    var url;
    if (selected.type == "image/png") {
      url = `https://firebasestorage.googleapis.com/v0/b/photos-app-87491.appspot.com/o/${
        selected.name + ".png"
      }?uploadType=resumable`;
    } else if (selected.type == "image/jpeg") {
      url = `https://firebasestorage.googleapis.com/v0/b/photos-app-87491.appspot.com/o/${
        selected.name + ".jpeg"
      }?uploadType=resumable`;
    }
    if (selected && accepted.includes(selected.type)) {
      setFile(selected);
      console.log(selected.type, selected.name);
      //https://firebasestorage.googleapis.com/v0/b/photos-app-87491.appspot.com/o/ball.jpeg
      const response = await fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": selected.type, // Set the content type based on the file type
        },
        body: formData,
      });
      console.log(response);
    } else {
      alert("Application only accepts PNG,JPEG and JPG fromats!!!");
      setFile(null);
    }
  }
  return (
    <>
      <div
        style={{
          display: "flex",
          height: "100vh",
        }}
      >
        <Container
          style={{
            display: "flex",
            width: "100%",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Row>
            <Col>
              <form>
                <input type="file" onChange={handleUpload} />
              </form>
            </Col>
          </Row>
        </Container>
      </div>
    </>
  );
}
export default Home;
