import { Col, Container, Row } from "react-bootstrap";
import { useState } from "react";

function Home() {
  const [file, setFile] = useState(null);
  const accepted = ["image/png", "image/jpeg", "image/jpg"];
  function handleUpload(e) {
    e.preventDefault();
    let selected = e.target.files[0];
    if (selected && accepted.includes(selected.type)) {
      setFile(selected);
    } else {
      alert("Application only accepts PNG,JPEG and JPG fromats!!!");
      setFile(null);
    }
  }
  return (
    <>
      <div
        style={{
          display: "flex",
          height: "100vh",
        }}
      >
        <Container
          style={{
            display: "flex",
            width: "100%",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Row>
            <Col>
              <form>
                <input type="file" onChange={handleUpload} />
              </form>
            </Col>
            <Col>{file && <Progress file={file} setFile={setFile} />}</Col>
          </Row>
        </Container>
      </div>
    </>
  );
}
export default Home;
