import { Col, Container, Row } from "react-bootstrap";
import { useState, useEffect } from "react";

function Home() {
  const [file, setFile] = useState(null);

  const accepted = ["image/png", "image/jpeg", "image/jpg"];

  async function handleUpload(e) {
    const formData = new FormData();
    formData.append("file", file);
    e.preventDefault();
    let selected = e.target.files[0];
    //const token = localStorage.getItem("token");
    var url = `https://firebasestorage.googleapis.com/v0/b/photos-app-87491.appspot.com/o/${selected.name}`;
    if (selected && accepted.includes(selected.type)) {
      setFile(selected);
      console.log(selected.type, selected.name);
      const response = await fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": "application/octet-stream", // Set the content type based on the file type
        },
        body: formData,
      });
      console.log(response);
    } else {
      alert("Application only accepts PNG,JPEG and JPG fromats!!!");
      setFile(null);
    }
  }
  useEffect(() => {
    async function fetchData() {
      const res = await fetch(
        "https://firebasestorage.googleapis.com/v0/b/photos-app-87491.appspot.com/o/ashish/"
      );
      console.log(res);
    }
    fetchData();
  }, []);
  return (
    <>
      <div
        style={{
          display: "flex",
          height: "100vh",
        }}
      >
        <Container>
          <Row>
            <Col>
              <img
                src="https://firebasestorage.googleapis.com/v0/b/photos-app-87491.appspot.com/o/mypic1.jpg"
                alt="pic1"
              />
            </Col>
          </Row>
        </Container>
        <Container
          style={{
            display: "flex",
            width: "100%",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Row>
            <Col>
              <form>
                <input type="file" onChange={handleUpload} />
              </form>
            </Col>
          </Row>
        </Container>
      </div>
    </>
  );
}
export default Home;
