import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import { useState, useRef } from "react";
import "./DatePicker.css";
import { Container } from "react-bootstrap";
function Cars({ cars }) {
  const [show, setShow] = useState(false);
  const date = useRef();
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  async function bookingHandler(id) {
    const x = String(date.current.value);
    const dateStr = x.replaceAll("-", "");
    const response = await fetch(
      `https://trippy-cars-default-rtdb.firebaseio.com/bookiings/${dateStr}`
    );
    if (response.ok) {
      console.log(response);
    } else {
      console.log("error");
    }
    setShow(false);
  }
  return (
    <>
      <Col
        xs={12}
        sm={12}
        md={12}
        lg={3}
        xl={3}
        xxl={3}
        onClick={handleShow}
        style={{
          cursor: "pointer",
        }}
      >
        <section
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "25px",
          }}
        >
          <Card style={{ width: "15rem" }}>
            <section
              style={{
                display: "flex",
                justifyContent: "center",
              }}
            >
              <Card.Img
                style={{ width: "150px", height: "200px" }}
                variant="top"
                src={cars.carImg.toString()}
              />
            </section>

            <Card.Body>
              <Card.Title
                style={{
                  display: "flex",
                  justifyContent: "center",
                }}
              >
                {cars.carName}
              </Card.Title>
              <Card.Text
                style={{
                  display: "flex",
                  justifyContent: "center",
                }}
              >
                Category:{cars.carCategory}
              </Card.Text>
              <Card.Text
                style={{
                  display: "flex",
                  justifyContent: "center",
                }}
              >
                Car No.:{cars.carNo.toLocaleUpperCase()}
              </Card.Text>
            </Card.Body>
          </Card>
        </section>
      </Col>
      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>Select Date of Booking</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <section
            style={{
              display: "flex",
              justifyContent: "center",
            }}
          >
            <img
              src={cars.carImg}
              alt={cars.carName}
              style={{
                display: "flex",
                justifyContent: "center",
                width: "100px",
                height: "150px",
              }}
            />
          </section>

          <h4
            style={{
              display: "flex",
              justifyContent: "center",
            }}
          >
            {cars.carName}
          </h4>
          <h4
            style={{
              display: "flex",
              justifyContent: "center",
            }}
          >
            {cars.carCategory}
          </h4>
          <h4
            style={{
              display: "flex",
              justifyContent: "center",
            }}
          >
            {cars.carNo.toLocaleUpperCase()}
          </h4>
          <section
            style={{
              display: "flex",
              justifyContent: "space-evenly",
              marginLeft: "-30%",
            }}
          >
            <span className="datepicker-toggle">
              <span className="datepicker-toggle-button"></span>
              <input
                type="date"
                className="datepicker-input"
                ref={date}
                id={cars.key}
              />
            </span>
          </section>
        </Modal.Body>
        <Modal.Footer>
          <Container>
            <p
              style={{
                display: "flex",
                justifyContent: "left",
                fontSize: "0.6rem",
              }}
            >
              Select a date of booking. If any other booking exist on that
              perticular date select next date or proceed with any another car.
            </p>
          </Container>
          <Button variant="dark" onClick={handleClose}>
            Close
          </Button>
          <Button variant="secondary" onClick={() => bookingHandler(cars.key)}>
            Book Now
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}
export default Cars;
