import { createSlice } from "@reduxjs/toolkit";
let val = false;
if (localStorage.getItem("isHost") === true) {
  val = true;
}
let isHosted = {
  isHosted: val,
};

const Host = createSlice({
  name: "host",
  initialState: isHosted,
  reducers: {
    hosted(state, action) {
      state.isHosted = true;
    },
  },
});
export default Host.reducer;
export const hostActions = Host.actions;
