import { Route, Switch } from "react-router-dom/cjs/react-router-dom";
import Login from "./Components/Authentication/Login";
import Signup from "./Components/Authentication/Signup";
import { Suspense } from "react";
// import Home from "./Components/HomePage/Home";
import { useSelector } from "react-redux";
import Welcome from "./WelcomePage/Welcome";
import Navigation from "./Components/Navigation/Navigation";
import Host from "./HostPage/Host";
import { useEffect } from "react";

function App() {
  const islogin = useSelector((state) => state.auth.isAuthenticated);
  useEffect(async () => {
    if (islogin) {
      const isHostRes = await fetch(
        `https://trippy-cars-default-rtdb.firebaseio.com/hostStatus/${user}.json`
      );
      const isHostData = await isHostRes.json();
      let targetValue;
      for (const key in isHostData) {
        targetValue = isHostData[key].isHost;
        localStorage.setItem("_id", key);
        break;
      }
      console.log(targetValue);
    }
  });
  return (
    <>
      <Navigation />
      <Suspense>
        <Switch>
          <Route path="/" exact>
            {islogin && <Welcome />}
            {!islogin && <Login />}
          </Route>
          <Route path="/Host" exact>
            <Host />
          </Route>
          <Route path="/Login" exact>
            <Login />
          </Route>
          <Route path="/Signup" exact>
            <Signup />
          </Route>
        </Switch>
      </Suspense>
    </>
  );
}
export default App;
