import { Container } from "react-bootstrap";
import HostForm from "./HostForm";
import HostDetails from "./HostDetails";
import { useState } from "react";

function Host() {
  const [isHost, setIsHost] = useState(false);

  return (
    <>
      <Container
        style={{
          marginTop: "2%",
        }}
      >
        {isHost && <HostForm />}
        {!isHost && <HostDetails />}
      </Container>
    </>
  );
}
export default Host;
