import { createSlice } from "@reduxjs/toolkit";
let isLoggedIn = {
  isAuthenticated: false,
};

const Auth = createSlice({
  name: "auth",
  initialState: isLoggedIn,
  reducers: {
    login(state, action) {
      state.isAuthenticated = true;
    },
  },
});
export default Auth.reducer;
export const authActions = Auth.actions;
